mapper.select:
SELECT product_common_no AS productCommonNo, product_common_name AS productCommonName, product_common_price AS productCommonPrice, product_common_description AS productCommonDescription, product_common_date AS productCommonDate FROM product_common WHERE category_no = #{categoryNo}
AND product_common_name LIKE '%${searchWord}%'
ORDER BY product_common_date DESC LIMIT #{beginRow}, #{rowPerPage}
